import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {Table} from 'react-bootstrap'
import { getAllStock } from '../../actions/inventoryAction'

const Inventory = () => {
    const {stock} = useSelector((state) => state.getAllStockReducer);
  
    const dispatch = useDispatch()
    useEffect(()=>{
        dispatch(getAllStock())//dispatch action
    }, [dispatch]);
  
  
  
    return (
    
    <>
    <h1>Inventory</h1>
    <br></br>

    {stock && stock.map((item) => (
      
    <div>
    <h1>{item.name}</h1>
          {//console.log(item.variants.length)
          //for(var i=0;i<5;i++)
          
          }
          
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Variant</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Warning</th>
              </tr>
            </thead>
            <tbody>
            {console.log("alll variants .. "+item.variants)}
            {console.log("variants 0.. "+item.variants[0])}
            {[...Array(item.variants.length)].map((x,i) =>
            (
            
                 <tr>
                    <td>
                    {item.variants[i]}
                    </td>

                    <td>
                    {item.price[i]}
                    </td>
                    
                    <td>
                    {item.varqty[i]}
                     
                    </td>
                    <td>{}</td>
                    
                  </tr>
               ))}
            </tbody>
          </Table>
      
        </div>

        ))} 
    </>
  )
}

export default Inventory
