import React, {useState} from 'react'
import { Container, Form, Button } from 'react-bootstrap'
import axios from 'axios';

const ResetScreen = () => {

    const [ email, setEmail] = useState('')
    const [exists,setExists] = useState(false);
    const [checked,setChecked] = useState(false);
    const [OTP, setOTP] = useState('');
    const [sent, setSent] = useState(false);
    const [matched, setMatched] = useState(false);


    const OTPHandler = async() =>{

        //sending API Request directly from here rather than making an action and reducer
        const reqobj = {
            email:email
        }
        console.log("Sending.. ",reqobj)
        const response = await axios.post("/api/users/finduser",reqobj);
        console.log("response recieved:", response.data.exists)
        setExists(response.data.exists);
        setChecked(true);
        
        //sending OTP
        if(exists)
        {
        let x = toString(Math.random() * 1000000);
        var mailobj = {
            to:email,
            subject:"PizzaHut: OTP for account verification",
            text:x
        }
        response = await axios.post("/api/mail/sendmail",mailobj);
        setSent(true);
    } 
       

        
    }

    const OTPMatcher = async() =>{

         
    }

    const passwordSender = async()=>{

    }

  return (
  
  <>
    
    
    <Container style={{"width":"30%", "margin-top":"5%","border":"1px solid lightgrey","padding":"2%"}}>
    <h3>Reset your Password</h3>
    <br></br><br></br>
    
    {!exists?
    
    <Form>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label>
        <Form.Control type="email" placeholder="Enter email" value={email} onChange={(e)=>setEmail(e.target.value)}/>
      </Form.Group>
        {email!=='' && checked && !exists ?<Form.Text className="text-muted">
          Email does not exist
        </Form.Text>:<h1></h1>}
      <Button variant="primary" onClick={OTPHandler}>
        SendOTP
      </Button>
    </Form>
    :
        <Container>{!matched?<Form>
        <center><h5 style={{"color":"lightgreen"}}>OTP sent to email successfully &check;</h5></center>
        <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>OTP</Form.Label>
            <Form.Control type="text" placeholder="Enter OTP" value={email} onChange={(e)=>setOTP(e.target.value)}/>
        </Form.Group>
            {OTP!=='' && sent && !matched ?<Form.Text className="text-muted">
            OTP does not match
            </Form.Text>:<h1></h1>}
            <Button variant="primary" onClick={passwordSender}>
            Send password on mail
            </Button>
            </Form>
        :   <>
            {alert('Password sent on mail')}
            {window.location.href('/login')}

            </>
        }</Container>
    }
    </Container>




    
    </>
  )
}

export default ResetScreen
