    const MailHandler=()=>{

        const dispatch =useDispatch();
        dispatch(sendMail('ekamjit105@gmail.com','Admin Stock Alert','testing text'))
    
    }
 
 
 
 return (<>
    <div>Testing</div>
    <Button onClick={MailHandler}>Test mailer</Button>
    </>
  )
}

