export const getAllStockReducer = (state={stock:[]}, action)=>{
    switch(action.type)
    {
        case "GET_STOCK_REQUEST":
            return{
                ...state, 
                loading:true
            };
        case "GET_STOCK_SUCCESS":
            return{
                stock:action.payload, 
                loading:false
            };
        case "GET_STOCK_FAIL":
                return{
                    error:action.payload, 
                    loading:false
                };
        default:
            return state;
    }
 }

