import axios from "axios";

export const sendMail = ({mailobj}) =>async(dispatch)=>{
    dispatch('SEND_MAIL_SUCCESS');
    try{
    const response =  await axios.post('mail/sendmail',mailobj);
    dispatch('SEND_MAIL_SUCCESS');
    }
    catch{
        dispatch('SEND_MAIL_FAIL');
    }
}
